import React from 'react';
import logo from '../assets/images/logopag.png';
import { Link } from 'react-router-dom';
import { useProducts } from '../context/context';
import { ShoppingCar } from '../Pages/PagesComponents/ShoppingCar';

export function Header(){
    const { setOpenCar, products, openCar } = useProducts()
    return( 
    <header>
        <div className="container-header">
            <ShoppingCar products={products} setOpen={setOpenCar} open={openCar}></ShoppingCar>
            <img src={logo} className="logo-image" alt=""/>
            <div className="input-container">
                <input className="buscador" type="text" placeholder="Buscar..."/>
                <i className="fa-solid fa-magnifying-glass iconsearch"></i>
            </div>

            <div className="carrito_perfil">
                <Link to="/inicio">Inicio</Link>
                <Link to="/backend">Backend</Link>
                <div className='carCount'>
                    <h6 className='countProducts'>Cantidad de productos</h6>
                    <i onClick={() => setOpenCar(true)} className="fa-sharp fa-solid fa-cart-shopping"></i>
                </div>
                <p className='separador'>|</p>

                <div className="image-perfil">
                    <img className="perfil-image" src={logo} alt=""/>
                </div>

            </div>
        </div>
    </header>
    )
}
